  //         // navigate("/jobSearch")
  //         console.log(response)
  //     })
  //     .catch(error => console.log(error));
  //     return("success")
  //     }
  //     }

  const verf = passwordVerification == userData.password ? true : false;

  function onRegisteronClick() {
    if (
      !(
        userData.password && userData.username&&passwordVerification
      )
    ) {
      setError("You must fill all field!");
      return;
    }

    axios.post('/api/users/authenticate', userData)
                    .then(response => {
                        navigate("/jobSearch")
                        console.log(response)
                    })
                    .catch(error => console.log(error));
  }

  //   useEffect(verf, [passwordVerification]);

  return (
    <div>
      <NavBar />
      <section className="form">
        {/* {errorMsg} */}
        {/* <h1>Register</h1> */}
        <h5>Username:</h5>
        <input
          className="input"
          value={userData.username}
          onChange={(e) => {
            const username = e.target.value;
            setUserData({
              ...userData,
              username: username,
            });
          }}
        />
        <h5>Password:</h5>
        <input
          className="input"
          value={userData.password}
          onChange={(e) => {
            const password = e.target.value;
            setUserData({
              ...userData,
              password: password,
            });
          }}
          //   type="password"
        />
        <h5>Password Verification:</h5>
        <input
