import { useState } from 'react';
import axios, { Axios } from 'axios';
import { now } from 'mongoose';

// 这个地方叫App有什么特殊含义吗？是不是home 的意思
function App() {
  const [formInput, setFormInput] = useState('');
  const [searchedJob, setSearchedJob] = useState({
    title: undefined,
    location: undefined,
    companyName: undefined,
    description: undefined,
    employerEmail: undefined,
    companyLink: undefined,
    postdate: undefined,
  })
  const [errorMsg, setError] = useState(null);
  // 当我click，value in the input filed会被searched on the back end,
  // 获得信息后，改变前端
  function onSearchButtonClick() {

    if (!formInput) {
      setError("You must type in a job title");
      return;
    }
    // forminput是看输入的是啥小精灵 
    axios.get('/api/job/find/' + formInput)
      .then(response => {
          setSearchedJob(response.data)
        navigate("/Register")})
      .catch(error => setError(error));
    console.log("hello, there");

    // doSomething();
  }

  return (
    <div>
      {errorMsg}
      <input type='text' value={formInput}
      onChange={(e) => {
        setError(null);
        setFormInput(e.target.value)

      }} />
      <button onClick={onSearchButtonClick}>
        Search Jobs
      </button>

      <div>
        Searched Job Title: {searchedJob}
      </div>
    </div> 
  );
}

export default App;
